<script>
	$(document).ready(function(){
		$('form.step1').submit(function(e){
			e.preventDefault();
			$('#modal--thankyou').modal('show');
			$(this).trigger("reset");


			setTimeout(function(){
				$('.firework').hide('3000');
			},3000)

			setTimeout(function(){
				$('#modal--thankyou').modal('hide');
			},10000)

		})

	})
	
</script>

